/** * Created by wangxiaoyu on 2017/8/12. */// var react = require('react')import React from 'react';// import less from './index.less';import {render} from 'react-dom';import Root from './root';import {AppContainer} from 'react-hot-loader';// console.log(react.version);render(    <AppContainer>        <Root/>    </AppContainer>,    document.getElementById('root'));    if(module.hot){        module.hot.accept('./root',()=>{            const NewRoot = require('./root').default;            render(                <AppContainer>                    <NewRoot/>                </AppContainer>,                document.getElementById('root')            )        })    }